package Exercises;

public class MathMethods {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
        //power method for exponents
		System.out.println(Math.pow(6, 2));
		
		//absolute value method
		System.out.println(Math.abs(-33.5));
		System.out.println(Math.abs(33.5));
		
		//ceil method
		System.out.println(Math.ceil(33.3));
		
		//floor method
		System.out.println(Math.floor(33.7));
		
		//max method
		System.out.println(Math.max(30.3, 15.4));
		
		//min method
		System.out.println(Math.min(30.3,15.4));
		
		//sqrt method
		System.out.println(Math.sqrt(36));
		
		
		
	}

}
